import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import Login from "./components/Login";
import { authenticate, retrieveItems, main } from "./services/AuthService";

function App() {
  const [state, setState] = useState({
    email: "",
    password: "",
    isLoggedIn: false
  });

  const LogIn = () =>
    setState({
      ...state,
      isLoggedIn: true
    });

  function onClickSignIn(event, email, password) {
    const newToken = authenticate();
    setState({
      email,
      password,
      isLoggedIn: true,
      token: newToken
    });

    event.preventDefault();

    if (state.email === "YNAPEyJk" && state.password === "ylYJDgFmnAIs") {
      return alert("sukces");
    } else if (state.email === "") {
      return alert("Proszę podać login");
    } else if (state.password === "") {
      return alert("Proszę podać hasło");
    } else {
      return alert("Nieprawidłowy login lub hasło");
    }
  }

  function onLoginChange(event) {
    setState({
      ...state,
      email: event.target.value
    });
  }

  function onPasswordChange(event) {
    setState({
      ...state,
      password: event.target.value
    });
  }

  return (
    <>
      {console.log(state)}
      {state.isLoggedIn ? (
        <div className="App">dupa</div>
      ) : (
        <div className="App">
          <Login
            onClick={(event, email, password) =>
              onClickSignIn(event, email, password)
            }
            onLoginChange={event => onLoginChange(event)}
            onPasswordChange={event => onPasswordChange(event)}
            email={state.email}
            password={state.password}
          />
        </div>
      )}
    </>
  );
}

export default App;
