import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Login from "./components/Login"
import { authenticate, retrieveItems, addItem, main } from "./services/AuthService"
import Table from "@material-ui/core/Table"
import TableBody from "@material-ui/core/Table"
import TableHeader from "@material-ui/core/Table"
import TableHeaderColumn from "@material-ui/core/Table"
import TableRow from "@material-ui/core/Table"
import TableRowColumn from "@material-ui/core/Table"

function App() {
    const [state, setState] = useState({
        email: '',
        password: '',
        isLoggedIn: false,
    })

    const LogIn = () => setState({
        ...state,
        isLoggedIn: true})

    function onClickSignIn(event, email, password) {
        const newitems = main().then(items => setState({
            email,
            password,
            isLoggedIn: true,
            items
        }))

        event.preventDefault()

                if (state.email === "YNAPEyJk" && state.password === "ylYJDgFmnAIs") {
                    return alert("sukces")
                } else if (state.email === ""){
                    return alert("Proszę podać login")
                } else if (state.password === ""){
                    return alert("Proszę podać hasło")
                } else {
                    return alert("Nieprawidłowy login lub hasło")
                }

    }

function onLoginChange(event){
     setState({
        ...state,
        email: event.target.value
})}

function onPasswordChange(event){
    setState({
       ...state,
       password: event.target.value,
})}

const OnClick = () => {
    console.log("click");

    const name = "abc";
    const item = { name };

    addItem(item).catch(error => console.error(error));
  };

  function ItemsTable(){
    return (    <Table>
        <TableHeader>
            <TableRow>           
                <TableHeaderColumn>ID</TableHeaderColumn>
                <TableHeaderColumn>NAME</TableHeaderColumn>
            </TableRow>
        </TableHeader>
        {state.items.map(item => (
        <TableBody>
            <TableRow>           
                <TableRowColumn>{item.id}</TableRowColumn>
            </TableRow>
            <TableRow> 
            <TableRowColumn>{item.name}</TableRowColumn>
            </TableRow>
        </TableBody>
        ))}
    </Table>)
}

  return ( <>{console.log(state)}
    {state.isLoggedIn ?
    <>
        <ItemsTable />
       <AddButton OnClick={OnClick}/>
       <DeleteButton />
       </> :
    <div className="App">     
        <Login 
        onClick={(event, email, password) => onClickSignIn(event, email, password)} 
        onLoginChange={(event) => onLoginChange(event)}
        onPasswordChange={(event) => onPasswordChange(event)}
        email={state.email} 
        password={state.password}/>
    </div>}
  </>);
}

function AddButton(props) {
    const { OnClick } = props
    return(
        <button
        onClick={()=>OnClick()}
      >
       Dodaj
      </button>
    )
    }

function DeleteButton(props) {
    const { OnClick } = props
    return(
        <button
        onClick={()=>OnClick()}
    >
    Usuń
    </button>
    )
}


export default App;
