import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Login from "./components/Login"

const REST_API_URL = "http://frontend-recruitment.one2tribe.pl:8080";
// const REST_API_URL = 'http://localhost:3000';

const AUTH_OPTIONS = {
  credentials: "include",
  mode: "cors"
};

export const authenticate = async () => {
  const body = JSON.stringify({
    username: "YNAPEyJk",
    password: "ylYJDgFmnAIs"
  });

  const response = await fetch(`${REST_API_URL}/api/authenticate`, {
    method: "post",
    ...AUTH_OPTIONS,
    body,
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json"
    }
  });
  setTimeout(() => {
    // const newResponse = response.getAllResponseHeaders();
    console.log(response);
  }, 3000);

  return response.text();
};

export const retrieveItems = async () => {
  const response = await fetch(`${REST_API_URL}/api/v1/item`, {
    ...AUTH_OPTIONS,
    headers: {
      Authorization:
        "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJpc3MiOiJzZWN1cmUtYXBpIiwiYXVkIjoic2VjdXJlLWFwcCIsInN1YiI6IllOQVBFeUprIiwiZXhwIjoxNTY4Mjg1MTE4LCJyb2wiOlsiVXNlciJdfQ.JP3fYRcp1afsl1Kj40qTyoo7FOzfWAyug7cV-YhynAF7Qr2CbdBIcYIsy6u89pDSBP2XdBk7yt_ScmiYPoRMmg",
      "Content-Type": "application/json",
      Accept: "application/json"
    }
  });

  return response.json();
};

export const addItem = async (item) => {
  const response = await fetch(`${REST_API_URL}/api/v1/item`, {
    method: "post",
    ...AUTH_OPTIONS,
    body: JSON.stringify(item),
    headers: {
      Authorization:
        "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJpc3MiOiJzZWN1cmUtYXBpIiwiYXVkIjoic2VjdXJlLWFwcCIsInN1YiI6IllOQVBFeUprIiwiZXhwIjoxNTY4Mjg1MTE4LCJyb2wiOlsiVXNlciJdfQ.JP3fYRcp1afsl1Kj40qTyoo7FOzfWAyug7cV-YhynAF7Qr2CbdBIcYIsy6u89pDSBP2XdBk7yt_ScmiYPoRMmg",
      "Content-Type": "application/json",
      Accept: "application/json"
    }
  });
  setTimeout(() => {
    // const newResponse = response.getAllResponseHeaders();
    console.log(response);
  }, 2000)
  return response.json();
};

const button = document.createElement("button");

button.onclick = () => {
  console.log("click");

  const name = "abc";
  const item = { name };

  addItem(item).catch(error => console.error(error));
};

button.textContent = "Dodaj"

document.body.appendChild(button)
function Button() {
    const OnClick = () => {
        console.log("click");

        const name = "abc";
        const item = { name };

        addItem(item).catch(error => console.error(error));
      };

return(
    <button
    onClick={()=>OnClick()}
  >
   Dodaj
  </button>
)
}

function App() {
    const [state, setState] = useState({
        email: '',
        password: '',
        isLoggedIn: false,
    })

    const LogIn = () => setState({
        ...state,
        isLoggedIn: true})

    function onClickSignIn(event, email, password) {
        const newitems = main().then(items => setState({
            email,
            password,
            isLoggedIn: true,
            items
        }))

        event.preventDefault()

                if (state.email === "YNAPEyJk" && state.password === "ylYJDgFmnAIs") {
                    return alert("sukces")
                } else if (state.email === ""){
                    return alert("Proszę podać login")
                } else if (state.password === ""){
                    return alert("Proszę podać hasło")
                } else {
                    return alert("Nieprawidłowy login lub hasło")
                }

    }

function onLoginChange(event){
     setState({
        ...state,
        email: event.target.value
})}

function onPasswordChange(event){
    setState({
       ...state,
       password: event.target.value,
})}

  return ( <>{console.log(state)}
    {state.isLoggedIn ?
    <>
    <div className="App">     
        {state.items.map(item => (
            <p>{item.id}{item.name}</p>
       ))}</div>
       <Button /></> :
    <div className="App">     
        <Login 
        onClick={(event, email, password) => onClickSignIn(event, email, password)} 
        onLoginChange={(event) => onLoginChange(event)}
        onPasswordChange={(event) => onPasswordChange(event)}
        email={state.email} 
        password={state.password}/>
    </div>}
  </>);
}

export default App;
