    <div className="App">     
        <Login 
        onClick={(event, email, password) => onClickSignIn(event, email, password)} 
        onLoginChange={(event) => onLoginChange(event)}
        onPasswordChange={(event) => onPasswordChange(event)}
        email={state.email} 
        password={state.password}/>
    </div>
  );
}

export default App;
